function adjustSilexWindowSize(h){var aa="flashcontent";set_frame_prop(aa,'height',h);}
function move(ba,x,y,w,h){set_frame_prop(ba,'top',y);set_frame_prop(ba,'left',x);set_frame_prop(ba,'width',w);set_frame_prop(ba,'height',h);}
function set_frame_prop(ca,da,ea){get_frame(ca)[da]=ea;}
function refresh_frame(fa){get_obj(fa).innerHTML=get_obj(fa).innerHTML;}
function get_frame(ga){return get_obj(ga).style;}
function get_obj(ha){if(document.layers){return document.layers[ha].document;}
else if(parseInt(navigator.appVersion)>=5&&navigator.appName=="Netscape"){return document.getElementById(ha);}
else if(document.all){return document.all[ha];}
return null;}