INSERT INTO `wp1_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(62, 1, '2009-08-07 12:01:41', '2009-08-07 10:01:41', '', 'article 2', '', 'inherit', 'open', 'open', '', '4-revision', '', '', '2009-08-07 12:01:41', '2009-08-07 10:01:41', '', 4, 'http://localhost/dev/1silex4wordpress/wordpress/?p=62', 0, 'revision', '', 0),
(28, 1, '2009-08-07 12:05:53', '2009-08-07 10:05:53', '<span style="color: #cccccc; font-family: verdana; font-size: 14px;">\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><span style="margin: 0px; padding: 0px; color: #ff8a0c;">Le blog SilexLabs.com est une initiative ayant pour objectif de donner une tribune d’expression aux différents membres de l’équipe Silex.</span></p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><span style="margin: 0px; padding: 0px; color: #ff8a0c;">Les laboratoires Silex sont une compilation de points de vues, expériences (…) ayant trait à Silex, au web, au graphisme, au monde logiciel libre (…)</span></p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><span style="margin: 0px; padding: 0px; color: #ff8a0c;">Les sujets abordés seront affinés au fil du temps.</span></p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><span style="margin: 0px; padding: 0px; color: #ff8a0c;">Merci d’avance pour votre soutien !</span></p>\r\n<p style="margin: 0px; padding: 5px; text-align: center;"><span style="margin: 0px; padding: 0px; color: #25ff48;"><br style="margin: 0px; padding: 0px;" /></span></p>\r\n<p style="margin: 0px; padding: 5px; text-align: center;"><span style="margin: 0px; padding: 0px; color: #25ff48;">**********************</span></p>\r\n<p style="margin: 0px; padding: 5px; text-align: center;"><span style="margin: 0px; padding: 0px; color: #1e90ff;"><br style="margin: 0px; padding: 0px;" /></span></p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><span style="margin: 0px; padding: 0px; color: #4811b2;">The SilexLabs blog is an initiative from the Silex team with one aim : to express ourselves and share our experiences, discoveries and stories during this month of may and beyond.</span></p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><span style="margin: 0px; padding: 0px; color: #4811b2;">The Silex Labs blog is a collection of points of view (…) concerning Silex, the Web, the software world, graphic design, Open Source projects (…)</span></p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><span style="margin: 0px; padding: 0px; color: #4811b2;">We’ll refine the topics raised in this blog as we go along…</span></p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><span style="margin: 0px; padding: 0px; color: #4811b2;">Thanks for your support !</span></p>\r\n\r\n</span>', 'page 1', '', 'publish', 'open', 'open', '', 'page-1', '', '', '2009-08-07 12:11:53', '2009-08-07 10:11:53', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?page_id=28', 0, 'page', '', 0),
(29, 1, '2009-08-07 12:07:58', '2009-08-07 10:07:58', '<span style="color: #cccccc; font-family: verdana; font-size: 14px;">\r\n<blockquote style="border: 1px dotted #3ba6e4; margin: 5px 15px; padding: 3px; font-size: 0.9em;">\r\n<p style="margin: 0px; padding: 5px;">Dear lexa,</p>\r\n<p style="margin: 0px; padding: 5px;">It has been a while since I was not active with my web site, that as you know is powered by your awesome SILEX.</p>\r\n<p style="margin: 0px; padding: 5px;">I’ve been very busy since the end of last year, and that prevented me from finishing up my web site (though it’s almost complete), and posting on your forum my case study. However, I haven’t forgot that obligation, and for sure I will do it as soon as I find some more time. My web site helped me boost my professional profile, and for sure it was a wise move for me to build it. Feedback I am receiving from those who saw it is always positive and enthusiastic.<span> </span><img style="margin: 0px; padding: 0px;" src="http://www.silexlabs.com/wp-includes/images/smilies/icon_smile.gif" alt=":)" /></p>\r\n<p style="margin: 0px; padding: 5px;">Today, I went to my google analytics account and was surprised that I had dozen of unique visits referring from silex-ria.org. And I went to your site, and found my (elvinkosova.com) site listed among photographers in your reference list. This makes me really proud of what I’ve accomplished with SILEX, and I am looking forward on updating my web site and contacting you soon.<span> </span><img style="margin: 0px; padding: 0px;" src="http://www.silexlabs.com/wp-includes/images/smilies/icon_smile.gif" alt=":)" /></p>\r\n<p style="margin: 0px; padding: 5px;">I hope you are doing well!</p>\r\n<p style="margin: 0px; padding: 5px;">Best regards…</p>\r\n<p style="margin: 0px; padding: 5px;">elvin…</p>\r\n</blockquote>\r\n<blockquote style="border: 1px dotted #3ba6e4; margin: 5px 15px; padding: 3px; font-size: 0.9em;">\r\n<p style="margin: 0px; padding: 5px;">Salut Alex,<br style="margin: 0px; padding: 0px;" />Je sais pas si tu te souviens, on s’est rencontré en octobre dernier chez mediabox puis on a but un coup a nation avec l’equipe Silex.<br style="margin: 0px; padding: 0px;" />En fin d’année dernière j’avais essayer de prendre en main silex, un peu galéré et pas trop eu le temps de à consacrer.<br style="margin: 0px; padding: 0px;" />J’ai remis le pied à l’étrier, et je trouve que l’évolution sur la v1.5 est énorme!<br style="margin: 0px; padding: 0px;" />Je suis votre blog avec attention et je ne manquerai pas de promouvoir ce superbe outil!<br style="margin: 0px; padding: 0px;" />Voila, juste pour vous dire bravo<br style="margin: 0px; padding: 0px;" />Philippe</p>\r\n</blockquote>\r\n<blockquote style="border: 1px dotted #3ba6e4; margin: 5px 15px; padding: 3px; font-size: 0.9em;">\r\n<div style="margin: 0px; padding: 0px;">J’adore votre blog. l’anniversaire de Sha avec un mot de toute l’équipe pour elle; le diaporama des chantiers de l’été, les photos de l’aventure à la Défense…</div>\r\n<div style="margin: 0px; padding: 0px;">c’est un formidable travail.</div>\r\n<div style="margin: 0px; padding: 0px;">Bravo.</div></blockquote>\r\n</span>', 'page 2', '', 'publish', 'open', 'open', '', 'page-2', '', '', '2009-08-07 12:13:34', '2009-08-07 10:13:34', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?page_id=29', 0, 'page', '', 0);
INSERT INTO `wp1_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(30, 1, '2009-08-07 12:07:58', '2009-08-07 10:07:58', '<span style="font-family: -webkit-sans-serif; font-size: 13px; line-height: 19px;">\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">Electric charge is a characteristic of some subatomic particles. It is quantized: when expressed in units of the so-called<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Elementary charge" href="/wiki/Elementary_charge">elementary charge</a><span> </span><em>e</em>, it takes integer or fractional values.<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electron" href="/wiki/Electron">Electrons</a><span> </span>by convention have a charge of -1, while<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Proton" href="/wiki/Proton">protons</a><span> </span>have the opposite charge of +1.<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Quark" href="/wiki/Quark">Quarks</a><span> </span>have a fractional charge of -<span style="white-space: nowrap;"><sup style="line-height: 1em;">1</sup><big>/</big><sub style="line-height: 1em;">3</sub></span><span> </span>or +<span style="white-space: nowrap;"><sup style="line-height: 1em;">2</sup><big>/</big><sub style="line-height: 1em;">3</sub></span>. The<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Antiparticle" href="/wiki/Antiparticle">antiparticle</a><span> </span>equivalents of these,<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Positron" href="/wiki/Positron">positrons</a>,<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Antiproton" href="/wiki/Antiproton">antiprotons</a><span> </span>and<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Antiquark" href="/wiki/Antiquark">antiquarks</a>, respectively, have the opposite charge. There are other<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Charged particle" href="/wiki/Charged_particle">charged particles</a>, as well. The discrete nature of electric charge was proposed by<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Michael Faraday" href="/wiki/Michael_Faraday">Michael Faraday</a><span> </span>in his<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electrolysis" href="/wiki/Electrolysis">electrolysis</a><span> </span>experiments, and then directly demonstrated by<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Robert Millikan" href="/wiki/Robert_Millikan">Robert Millikan</a>in his<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Oil-drop experiment" href="/wiki/Oil-drop_experiment">oil-drop experiment</a>.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">In general, same-sign charged particles repel one another, while different-sign charged particles attract. This is expressed quantitatively in<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Coulomb''s law" href="/wiki/Coulomb%27s_law">Coulomb''s law</a>, which states that the magnitude of the electrostatic repelling<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Force" href="/wiki/Force">force</a><span> </span>between two particles is proportional to the product of their charges and the<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Inverse square" href="/wiki/Inverse_square">inverse square</a><span> </span>of the distance between them.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">The electric charge of a<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Macroscopic" href="/wiki/Macroscopic">macroscopic</a><span> </span>object is the sum of the electric charges of its constituent particles. Often, the net electric charge is zero, because it is favorable for the number of electrons in every<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Atom" href="/wiki/Atom">atom</a><span> </span>to equal the number of<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Protons" href="/wiki/Protons">protons</a><span> </span>(or, more generally, for the number of anions, or negatively charged atoms, in every<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Molecule" href="/wiki/Molecule">molecule</a><span> </span>to equal the number of cations, or positively charged atoms). When the net electric charge is non-zero and motionless, one has the phenomenon known as<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Static electricity" href="/wiki/Static_electricity">static electricity</a>. Even when the net charge is zero, it can be distributed non-uniformly (e.g., due to an external<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electric field" href="/wiki/Electric_field">electric field</a>, or due to molecular motion), in which case the material is said to be<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Polarization density" href="/wiki/Polarization_density">polarized</a>. The charge due to the polarization is known as<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Bound charge" href="/wiki/Bound_charge">bound charge</a>, while the excess charge brought from outside is called<span> </span><em>free charge</em>. The motion of charged particles (e.g., of electrons in metals) in a particular direction is known as<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electric current" href="/wiki/Electric_current">electric current</a>.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;"><a id="Units" style="text-decoration: none; color: #002bb8; background-image: none;" name="Units"></a></p>\r\n\r\n<h2 style="border-bottom: 1px solid #aaaaaa; margin: 0px 0px 0.6em; color: black; background-image: none; font-weight: normal; padding-top: 0.5em; padding-bottom: 0.17em; font-size: 19px;"><span style="float: right; margin-left: 5px; font-size: 13px;">[<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Edit section: Units" href="/w/index.php?title=Electric_charge&amp;action=edit&amp;section=2">edit</a>]</span><span>Units</span></h2>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">The<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="SI" href="/wiki/SI">SI</a><span> </span>unit of<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Quantity of electricity" href="/wiki/Quantity_of_electricity">quantity of electric charge</a><span> </span>is the<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Coulomb" href="/wiki/Coulomb">coulomb</a>, which is equivalent to about<span> </span><span style="white-space: nowrap;">6.25 × 10<sup style="line-height: 1em;">18</sup><span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Elementary charge" href="/wiki/Elementary_charge">e</a></span><span> </span>(e is the charge on a single electron or proton). Hence, the charge of an electron is approximately<span style="white-space: nowrap;">-1.602 x 10<sup style="line-height: 1em;">-19</sup><span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Coulomb" href="/wiki/Coulomb">C</a></span>. The coulomb is defined as the quantity of charge that has passed through the cross-section of an<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electrical conductor" href="/wiki/Electrical_conductor">electrical conductor</a><span> </span>carrying one<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Ampere" href="/wiki/Ampere">ampere</a><span> </span>within one second. The symbol<span> </span><em>Q</em><span> </span>is often used to denote a quantity of electricity or charge. The quantity of electric charge can be directly measured with an<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electrometer" href="/wiki/Electrometer">electrometer</a>, or indirectly measured with a<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Galvanometer" href="/wiki/Galvanometer">ballistic galvanometer</a>.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">After finding the<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Quantized" href="/wiki/Quantized">quantized</a><span> </span>character of charge, in 1891<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="George Johnstone Stoney" href="/wiki/George_Johnstone_Stoney">Stoney</a><span> </span>proposed the unit ''electron'' for this fundamental unit of electrical charge. This was before the discovery of the particle by<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="J. J. Thomson" href="/wiki/J._J._Thomson">J.J. Thomson</a><span> </span>in 1897. Today, the name "electron" for the unit of charge is no longer widely used except in the derived unit "<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electronvolt" href="/wiki/Electronvolt">electronvolt</a>". This is quite surprising considering the wide use of this unit in the fields of physics and chemistry. The unit is today treated as nameless, referred to as "fundamental unit of charge" or simply as "e".</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">Formally, a measure of charge should be a multiple of the elementary charge<span> </span><em>e</em><span> </span>(charge is<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Quantized" href="/wiki/Quantized">quantized</a>), but since it is an average,<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Macroscopic" href="/wiki/Macroscopic">macroscopic</a><span> </span>quantity, many orders of<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Magnitude" href="/wiki/Magnitude">magnitude</a><span> </span>larger than a single elementary charge, it can effectively take on any<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Real number" href="/wiki/Real_number">real value</a>. Furthermore, in some contexts it is meaningful to speak of fractions of a charge; e.g. in the charging of a<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Capacitor" href="/wiki/Capacitor">capacitor</a>.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;"><a id="History" style="text-decoration: none; color: #002bb8; background-image: none;" name="History"></a></p>\r\n\r\n<h2 style="border-bottom: 1px solid #aaaaaa; margin: 0px 0px 0.6em; color: black; background-image: none; font-weight: normal; padding-top: 0.5em; padding-bottom: 0.17em; font-size: 19px;"><span style="float: right; margin-left: 5px; font-size: 13px;">[<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Edit section: History" href="/w/index.php?title=Electric_charge&amp;action=edit&amp;section=3">edit</a>]</span><span>History</span></h2>\r\n<div style="border-style: none; margin: 0.5em 0px 0.8em 1.4em; width: auto; clear: right; float: right; background-color: white;">\r\n<div style="border: 1px solid #cccccc; padding: 3px ! important; overflow: hidden; min-width: 100px; background-color: #f9f9f9; font-size: 12px; text-align: center; width: 182px;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Coulomb''s torsion balance" href="/wiki/File:Bcoulomb.png"><img style="border: 1px solid #cccccc; vertical-align: middle; background-color: #ffffff;" src="http://upload.wikimedia.org/wikipedia/commons/thumb/0/04/Bcoulomb.png/180px-Bcoulomb.png" alt="" width="180" height="232" /></a>\r\n<div style="border-style: none; padding: 3px ! important; text-align: left; line-height: 1.4em; font-size: 11px;">\r\n<div style="border-style: none ! important; float: right; background-image: none ! important;"><a style="border-style: none ! important; text-decoration: none; color: #002bb8; background-image: none ! important; display: block;" title="Enlarge" href="/wiki/File:Bcoulomb.png"><img style="border-style: none ! important; vertical-align: middle; display: block; background-image: none ! important; background-color: #ffffff;" src="/skins-1.5/common/images/magnify-clip.png" alt="" width="15" height="11" /></a></div>\r\nCoulomb''s<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Torsion balance" href="/wiki/Torsion_balance#Torsion_balance">torsion balance</a></div>\r\n</div>\r\n</div>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">As reported by the Ancient Greek philosopher<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Thales of Miletus" href="/wiki/Thales_of_Miletus">Thales of Miletus</a><span> </span>around 600 BC, charge (or<span> </span><em>electricity</em>) could be accumulated by rubbing<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Fur" href="/wiki/Fur">fur</a><span> </span>on various substances, such as<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Amber" href="/wiki/Amber">amber</a>. The Greeks noted that the charged amber buttons could attract light objects such as<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Hair" href="/wiki/Hair">hair</a>. They also noted that if they rubbed the amber for long enough, they could even get a spark to jump. This property derives from the<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Triboelectric effect" href="/wiki/Triboelectric_effect">triboelectric effect</a>.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">In 1600 the English scientist<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="William Gilbert" href="/wiki/William_Gilbert">William Gilbert</a><span> </span>returned to the subject in<span> </span><em>De Magnete</em>, and coined the<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="New Latin" href="/wiki/New_Latin">New Latin</a><span> </span>word<span> </span><em>electricus</em><span> </span>from<span> </span><em>??e?t???</em><span> </span>(<em>elektron</em>), the Greek word for "amber", which soon gave rise to the English words "electric" and "electricity." He was followed in 1660 by<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Otto von Guericke" href="/wiki/Otto_von_Guericke">Otto von Guericke</a>, who invented what was probably the first<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electrostatic generator" href="/wiki/Electrostatic_generator">electrostatic generator</a>. Other European pioneers were<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Robert Boyle" href="/wiki/Robert_Boyle">Robert Boyle</a>, who in 1675 stated that electric attraction and repulsion can act across a<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Vacuum" href="/wiki/Vacuum">vacuum</a>;<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Stephen Gray (scientist)" href="/wiki/Stephen_Gray_%28scientist%29">Stephen Gray</a>, who in 1729 classified materials as<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electrical conductor" href="/wiki/Electrical_conductor">conductors</a><span> </span>and<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electrical insulation" href="/wiki/Electrical_insulation">insulators</a>; and<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="C. F. du Fay" href="/wiki/C._F._du_Fay">C. F. du Fay</a>, who proposed in 1733<sup id="cite_ref-0" style="line-height: 1em; font-weight: normal; font-style: normal;"><a style="text-decoration: none; color: #002bb8; background-image: none; white-space: nowrap;" href="#cite_note-0"><span>[</span>1<span>]</span></a></sup><span> </span>that electricity came in two varieties which cancelled each other, and expressed this in terms of a two-fluid theory. When<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Glass" href="/wiki/Glass">glass</a><span> </span>was rubbed with<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Silk" href="/wiki/Silk">silk</a>, du Fay said that the glass was charged with<span> </span><em>vitreous electricity</em>, and when amber was rubbed with fur, the amber was said to be charged with<span> </span><em>resinous electricity</em>. In 1839,<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Michael Faraday" href="/wiki/Michael_Faraday">Michael Faraday</a><span> </span>showed that the apparent division between<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Static electricity" href="/wiki/Static_electricity">static electricity</a>,<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electric current" href="/wiki/Electric_current">current electricity</a>and<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Bioelectricity" href="/wiki/Bioelectricity">bioelectricity</a><span> </span>was incorrect, and all were a consequence of the behavior of a single kind of<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electricity" href="/wiki/Electricity">electricity</a><span> </span>appearing in opposite<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electrical polarity" href="/wiki/Electrical_polarity">polarities</a>. It is arbitrary which polarity you call positive and which you call negative. Positive charge can be defined as the charge left on a glass rod after being rubbed with silk.<sup id="cite_ref-1" style="line-height: 1em; font-weight: normal; font-style: normal;"><a style="text-decoration: none; color: #002bb8; background-image: none; white-space: nowrap;" href="#cite_note-1"><span>[</span>2<span>]</span></a></sup></p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">One of the foremost experts on electricity in the 18th century was<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Benjamin Franklin" href="/wiki/Benjamin_Franklin">Benjamin Franklin</a>, who argued in favour of a one-fluid theory of electricity. Franklin imagined electricity as being a type of invisible fluid present in all matter; for example he believed that it was the<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Glass" href="/wiki/Glass">glass</a><span> </span>in a<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Leyden jar" href="/wiki/Leyden_jar">Leyden jar</a><span> </span>that held the accumulated charge. He posited that rubbing insulating surfaces together caused this fluid to change location, and that a flow of this fluid constitutes an electric current. He also posited that when matter contained too little of the fluid it was "negatively" charged, and when it had an excess it was "positively" charged. Arbitrarily (or for a reason that was not recorded) he identified the term "positive" with vitreous electricity and "negative" with resinous electricity.<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="William Watson (scientist)" href="/wiki/William_Watson_%28scientist%29">William Watson</a><span> </span>arrived at the same explanation at about the same time.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;"><a id="Static_electricity_and_electric_current" style="text-decoration: none; color: #002bb8; background-image: none;" name="Static_electricity_and_electric_current"></a></p>\r\n\r\n<h2 style="border-bottom: 1px solid #aaaaaa; margin: 0px 0px 0.6em; color: black; background-image: none; font-weight: normal; padding-top: 0.5em; padding-bottom: 0.17em; font-size: 19px;"><span style="float: right; margin-left: 5px; font-size: 13px;">[<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Edit section: Static electricity and electric current" href="/w/index.php?title=Electric_charge&amp;action=edit&amp;section=4">edit</a>]</span><span>Static electricity and electric current</span></h2>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Static electricity" href="/wiki/Static_electricity">Static electricity</a><span> </span>and<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electric current" href="/wiki/Electric_current">electric current</a><span> </span>are two separate phenomena, both involving electric charge, and may occur simultaneously in the same object. Static electricity is a reference to the electric charge of an object and the related<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electrostatic discharge" href="/wiki/Electrostatic_discharge">electrostatic discharge</a><span> </span>when two objects are brought together that are not at equilibrium. An electrostatic discharge creates a change in the charge of each of the two objects. In contrast, electric current is the flow of electric charge through an object, which produces no net loss or gain of electric charge. Although charge flows between two objects during an electrostatic discharge, time is too short for current to be maintained.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;"><a id="Electrification_by_friction" style="text-decoration: none; color: #002bb8; background-image: none;" name="Electrification_by_friction"></a></p>\r\n\r\n<h3 style="margin: 0px 0px 0.3em; color: black; background-image: none; font-weight: bold; padding-top: 0.5em; padding-bottom: 0.17em; border-bottom-style: none; font-size: 17px;"><span style="float: right; margin-left: 5px; font-size: 13px; font-weight: normal;">[<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Edit section: Electrification by friction" href="/w/index.php?title=Electric_charge&amp;action=edit&amp;section=5">edit</a>]</span><span>Electrification by friction</span></h3>\r\n<div style="font-style: italic; padding-left: 2em; margin-bottom: 0.5em;">Further information:<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Triboelectric effect" href="/wiki/Triboelectric_effect">triboelectric effect</a></div>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">Experiment I</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">Let a piece of glass and a piece of resin, neither of which exhibits any electrical properties, be rubbed together and left with the rubbed surfaces in contact. They will still exhibit no electrical properties. Let them be separated. They will now attract each other.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">If a second piece of glass be rubbed with a second piece of resin, and if the piece be then separated and suspended in the neighbourhood of the former pieces of glass and resin, it may be observed:</p>\r\n\r\n<ol style="margin: 0.3em 0px 0.5em 3.2em; padding: 0px; line-height: 1.5em; list-style-image: none;">\r\n	<li style="margin-bottom: 0.1em;">that the two pieces of glass repel each other.</li>\r\n	<li style="margin-bottom: 0.1em;">that each piece of glass attracts each piece of resin.</li>\r\n	<li style="margin-bottom: 0.1em;">that the two pieces of resin repel each other.</li>\r\n</ol>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">These phenomena of attraction and repulsion are called electrical phenomena and the bodies which exhibit them are said to be ''electrified'', or to be ''charged with electricity''.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">Bodies may be electrified in many other ways, as well as by friction.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">The electrical properties of the two pieces of glass are similar to each other but opposite to those of the two pieces of resin: the glass attracts what the resin repels and repels what the resin attracts.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">If a body electrified in any manner whatever behaves as the glass does, that is, if it repels the glass and attracts the resin, the body is said to be ''vitreously'' electrified, and if it attracts the glass and repels the resin it is said to be ''resinously'' electrified. All electrified bodies are found to be either vitreously or resinously electrified.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">It is the established practice of men of science to call the vitreous electrification positive, and the resinous electrification negative. The exactly opposite properties of the two kinds of electrification justify us in indicating them by opposite signs, but the application of the positive sign to one rather than to the other kind must be considered as a matter of arbitrary convention, just as it is a matter of convention in<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Mathematical diagram" href="/wiki/Mathematical_diagram">mathematical diagram</a><span> </span>to reckon positive distances towards the right hand.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">No force, either of attraction or of repulsion, can be observed between an electrified body and a body not electrified.<sup id="cite_ref-2" style="line-height: 1em; font-weight: normal; font-style: normal;"><a style="text-decoration: none; color: #002bb8; background-image: none; white-space: nowrap;" href="#cite_note-2"><span>[</span>3<span>]</span></a></sup></p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">We now know that the Franklin/Watson model was fundamentally correct. There is only one kind of electrical charge, and only one variable is required to keep track of the amount of charge.<sup id="cite_ref-one-kind_3-0" style="line-height: 1em; font-weight: normal; font-style: normal;"><a style="text-decoration: none; color: #002bb8; background-image: none; white-space: nowrap;" href="#cite_note-one-kind-3"><span>[</span>4<span>]</span></a></sup><span> </span>On the other hand, just knowing the charge is not a complete description of the situation. Matter is composed of several kinds of electrically charged particles, and these particles have many properties, not just charge.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">The most common charge carriers are the positively charged<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Proton" href="/wiki/Proton">proton</a><span> </span>and the negatively charged<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electron" href="/wiki/Electron">electron</a>. The movement of any of these charged particles constitutes an electric current. In many situations, it suffices to speak of the<span> </span><em><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Conventional current" href="/wiki/Conventional_current">conventional current</a></em><span> </span>without regard to whether it is carried by positive charges moving in the direction of the conventional current and/or by negative charges moving in the opposite direction. This macroscopic viewpoint is an approximation that simplifies electromagnetic concepts and calculations.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">At the opposite extreme, if one looks at the microscopic situation, one sees there are many ways of carrying an<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electric current" href="/wiki/Electric_current">electric current</a>, including: a flow of electrons; a flow of electron "<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electron hole" href="/wiki/Electron_hole">holes</a>" which act like positive particles; and both negative and positive particles (<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Ion" href="/wiki/Ion">ions</a><span> </span>or other charged particles) flowing in opposite directions in an<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electrolyte" href="/wiki/Electrolyte">electrolytic</a><span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Solution" href="/wiki/Solution">solution</a><span> </span>or a<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Plasma (physics)" href="/wiki/Plasma_%28physics%29">plasma</a>).</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">Beware that in the common and important case of metallic wires, the direction of the conventional current is opposite to the drift velocity of the actual charge carriers, i.e. the electrons. This is a source of confusion for beginners.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;"><a id="Properties" style="text-decoration: none; color: #002bb8; background-image: none;" name="Properties"></a></p>\r\n\r\n<h2 style="border-bottom: 1px solid #aaaaaa; margin: 0px 0px 0.6em; color: black; background-image: none; font-weight: normal; padding-top: 0.5em; padding-bottom: 0.17em; font-size: 19px;"><span style="float: right; margin-left: 5px; font-size: 13px;">[<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Edit section: Properties" href="/w/index.php?title=Electric_charge&amp;action=edit&amp;section=6">edit</a>]</span><span>Properties</span></h2>\r\n</span>\r\n<table style="border: 1px solid #aaaaaa; margin: 0.5em 0px 0.5em 1em; padding: 0.2em; font-size: 11px; color: black; background-color: #f9f9f9; float: right; clear: right; width: 19em; text-align: left;" border="0" cellspacing="2">\r\n<tbody>\r\n<tr>\r\n<td style="vertical-align: top; background-color: #efefef; font-size: 13px;" align="center">\r\n<div style="text-align: center; position: relative; white-space: nowrap;"><strong><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Flavour (particle physics)" href="/wiki/Flavour_%28particle_physics%29">Flavour</a><span> </span>in<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Particle physics" href="/wiki/Particle_physics">particle physics</a></strong>\r\n<div style="margin: 0px 5px; padding: 0px; background-image: none; font-weight: normal; font-size: xx-small; position: absolute; right: 0px; top: 0px;"><a style="padding: 0px ! important; text-decoration: none; color: #002bb8; background-image: none ! important;" title="Template:Flavour quantum numbers" href="/wiki/Template:Flavour_quantum_numbers"><span title="View this template">v</span></a> <span style="font-size: 7px;">•</span> <a style="padding: 0px ! important; text-decoration: none; color: #002bb8; background-image: none ! important;" title="Template talk:Flavour quantum numbers" href="/wiki/Template_talk:Flavour_quantum_numbers"><span title="Discuss this template">d</span></a> <span style="font-size: 7px;">•</span> <a style="text-decoration: none; color: #3366bb; background-image: none ! important; padding-top: 0px ! important; padding-right: 0px ! important; padding-bottom: 0px ! important; padding-left: 0px;" title="http://en.wikipedia.org/w/index.php?title=Template:Flavour_quantum_numbers&amp;action=edit" rel="nofollow" href="http://en.wikipedia.org/w/index.php?title=Template:Flavour_quantum_numbers&amp;action=edit"><span title="Edit this template">e</span></a></div>\r\n</div></td>\r\n</tr>\r\n<tr>\r\n<td style="vertical-align: top;"><strong>Flavour<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Quantum number" href="/wiki/Quantum_number">quantum numbers</a>:</strong>\r\n<ul style="margin: 0.3em 0px 0.5em 1.5em; padding: 0px; line-height: 1.5em; list-style-type: square; list-style-image: url(http://en.wikipedia.org/skins-1.5/monobook/bullet.gif);">\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Baryon number" href="/wiki/Baryon_number">Baryon number</a>: B</li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Lepton number" href="/wiki/Lepton_number">Lepton number</a>: L</li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Strangeness (particle physics)" href="/wiki/Strangeness_%28particle_physics%29">Strangeness</a>: S</li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Charm (quantum number)" href="/wiki/Charm_%28quantum_number%29">Charmness</a>: C</li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Bottomness" href="/wiki/Bottomness">Bottomness</a>: B''</li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Topness" href="/wiki/Topness">Topness</a>: T</li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Isospin" href="/wiki/Isospin">Isospin</a>:<span> </span><strong>I</strong><span> </span>or I<sub style="line-height: 1em;">z</sub></li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Weak isospin" href="/wiki/Weak_isospin">Weak isospin</a>:<span> </span><strong>T</strong><span> </span>or T<sub style="line-height: 1em;">z</sub></li>\r\n	<li style="margin-bottom: 0.1em;"><strong>Electric charge</strong>: Q</li>\r\n</ul>\r\n<hr style="border-width: 0px; margin: 0.2em 0px; height: 1px; color: #aaaaaa; background-color: #aaaaaa;" />\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;"><strong>Combinations:</strong></p>\r\n\r\n<ul style="margin: 0.3em 0px 0.5em 1.5em; padding: 0px; line-height: 1.5em; list-style-type: square; list-style-image: url(http://en.wikipedia.org/skins-1.5/monobook/bullet.gif);">\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Hypercharge" href="/wiki/Hypercharge">Hypercharge</a>: Y\r\n<ul style="margin: 0.3em 0px 0.5em 1.5em; padding: 0px; line-height: 1.5em; list-style-type: square; list-style-image: url(http://en.wikipedia.org/skins-1.5/monobook/bullet.gif);">\r\n	<li style="margin-bottom: 0.1em;">Y = (B + S + C + B'' + T)</li>\r\n	<li style="margin-bottom: 0.1em;">Y = 2 (Q - I<sub style="line-height: 1em;">z</sub>)</li>\r\n</ul>\r\n</li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Weak hypercharge" href="/wiki/Weak_hypercharge">Weak hypercharge</a>: Y<sub style="line-height: 1em;">W</sub>\r\n<ul style="margin: 0.3em 0px 0.5em 1.5em; padding: 0px; line-height: 1.5em; list-style-type: square; list-style-image: url(http://en.wikipedia.org/skins-1.5/monobook/bullet.gif);">\r\n	<li style="margin-bottom: 0.1em;">Y<sub style="line-height: 1em;">W</sub><span> </span>= 2 (Q - T<sub style="line-height: 1em;">z</sub>)</li>\r\n	<li style="margin-bottom: 0.1em;">X + 2Y<sub style="line-height: 1em;">W</sub><span> </span>= 5 (<a style="text-decoration: none; color: #002bb8; background-image: none;" title="B-L" href="/wiki/B-L">B - L</a>)</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<hr style="border-width: 0px; margin: 0.2em 0px; height: 1px; color: #aaaaaa; background-color: #aaaaaa;" /><dl style="margin-top: 0.2em; margin-bottom: 0.5em;"><dt style="font-weight: bold; margin-bottom: 0.1em;">Flavour mixing</dt></dl>\r\n<ul style="margin: 0.3em 0px 0.5em 1.5em; padding: 0px; line-height: 1.5em; list-style-type: square; list-style-image: url(http://en.wikipedia.org/skins-1.5/monobook/bullet.gif);">\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Cabibbo–Kobayashi–Maskawa matrix" href="/wiki/Cabibbo%E2%80%93Kobayashi%E2%80%93Maskawa_matrix">CKM matrix</a></li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Pontecorvo–Maki–Nakagawa–Sakata matrix" href="/wiki/Pontecorvo%E2%80%93Maki%E2%80%93Nakagawa%E2%80%93Sakata_matrix">PMNS matrix</a></li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Quark–lepton complementarity" href="/wiki/Quark%E2%80%93lepton_complementarity">Flavour complementarity</a></li>\r\n</ul>\r\n</td>\r\n</tr>\r\n</tbody></table>\r\n<span style="font-family: -webkit-sans-serif; font-size: 13px; line-height: 19px;">\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">Aside from the properties described in articles about<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electromagnetism" href="/wiki/Electromagnetism">electromagnetism</a>, charge is a<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Theory of relativity" href="/wiki/Theory_of_relativity">relativistic</a><span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Invariant (physics)" href="/wiki/Invariant_%28physics%29">invariant</a>. This means that any particle that has charge<span> </span><em>Q</em>, no matter how fast it goes, always has charge<span> </span><em>Q</em>. This property has been experimentally verified by showing that the charge of<span> </span><em>one</em><span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Helium" href="/wiki/Helium">helium</a><span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Atomic nucleus" href="/wiki/Atomic_nucleus">nucleus</a><span> </span>(two<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Proton" href="/wiki/Proton">protons</a><span> </span>and two<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Neutron" href="/wiki/Neutron">neutrons</a><span> </span>bound together in a nucleus and moving around at high speeds) is the same as<span> </span><em>two</em><span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Deuterium" href="/wiki/Deuterium">deuterium</a><span> </span>nuclei (one proton and one neutron bound together, but moving much more slowly than they would if they were in a helium nucleus).</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;"><a id="Conservation_of_electric_charge" style="text-decoration: none; color: #002bb8; background-image: none;" name="Conservation_of_electric_charge"></a></p>\r\n\r\n<h2 style="border-bottom: 1px solid #aaaaaa; margin: 0px 0px 0.6em; color: black; background-image: none; font-weight: normal; padding-top: 0.5em; padding-bottom: 0.17em; font-size: 19px;"><span style="float: right; margin-left: 5px; font-size: 13px;">[<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Edit section: Conservation of electric charge" href="/w/index.php?title=Electric_charge&amp;action=edit&amp;section=7">edit</a>]</span><span>Conservation of electric charge</span></h2>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">The total electric charge of an<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Isolated system" href="/wiki/Isolated_system">isolated system</a><span> </span>remains constant regardless of changes within the system itself. This law is inherent to all processes known to physics and can be derived in a local form from<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Gauge invariance" href="/wiki/Gauge_invariance">gauge invariance</a><span> </span>of the<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Wave function" href="/wiki/Wave_function">wave function</a>. The conservation of charge results in the charge-current<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Continuity equation" href="/wiki/Continuity_equation">continuity equation</a>. More generally, the net change in<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Charge density" href="/wiki/Charge_density">charge density</a><span> </span><span style="font-size: 16px; line-height: 1.5em; font-family: serif; white-space: nowrap;">?</span><span> </span>within a volume of integration<span> </span><span style="font-size: 16px; line-height: 1.5em; font-family: serif; white-space: nowrap;"><em>V</em></span><span> </span>is equal to the area integral over the<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Current density" href="/wiki/Current_density">current density</a><span> </span><span style="font-size: 16px; line-height: 1.5em; font-family: serif; white-space: nowrap;"><em>J</em></span><span> </span>on the surface of the area<span> </span><span style="font-size: 16px; line-height: 1.5em; font-family: serif; white-space: nowrap;"><em>S</em></span>, which is in turn equal to the net<span> </span><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electric current" href="/wiki/Electric_current">current</a><span> </span><span style="font-size: 16px; line-height: 1.5em; font-family: serif; white-space: nowrap;"><em>I</em></span>:</p>\r\n\r\n<dl style="margin-top: 0.2em; margin-bottom: 0.5em;"><dd style="line-height: 1.5em; margin-left: 2em; margin-bottom: 0.1em;"><img style="border-style: none; vertical-align: middle;" src="http://upload.wikimedia.org/math/d/2/7/d272aaea3e2861e3cffd111190f704eb.png" alt="- \\frac{d}{dt} \\int_V \\rho \\, \\mathrm{d}V = \\int_S \\mathbf{J} \\cdot \\mathrm{d}\\mathbf{S} = \\int J dS \\cos\\theta = I." /></dd></dl>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">Thus, the conservation of electric charge, as expressed by the continuity equation, gives the result:</p>\r\n\r\n<dl style="margin-top: 0.2em; margin-bottom: 0.5em;"><dd style="line-height: 1.5em; margin-left: 2em; margin-bottom: 0.1em;"><img style="border-style: none; vertical-align: middle;" src="http://upload.wikimedia.org/math/d/d/d/ddd7614c58d6d1119b1750bddb2b5dec.png" alt="I = \\frac{dQ}{dt}." /></dd></dl>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">The charge transferred between times<span> </span><em>t</em><sub style="line-height: 1em;">o</sub><span> </span>and<span> </span><em>t</em><span> </span>is obtained by integrating both sides:</p>\r\n\r\n<dl style="margin-top: 0.2em; margin-bottom: 0.5em;"><dd style="line-height: 1.5em; margin-left: 2em; margin-bottom: 0.1em;"><img style="border-style: none; vertical-align: middle;" src="http://upload.wikimedia.org/math/4/b/a/4bab1058a91c834d8cd943dbc9ba73d9.png" alt="Q = \\int_{t_o}^{t_f} I\\, \\mathrm{d}t " /></dd></dl>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;">where<span> </span><strong>I</strong><span> </span>is the net outward current through a closed surface and<span> </span><strong>Q</strong><span> </span>is the electric charge contained within the volume defined by the surface.</p>\r\n<p style="margin: 0.4em 0px 0.5em; line-height: 1.5em;"><a id="See_also" style="text-decoration: none; color: #002bb8; background-image: none;" name="See_also"></a></p>\r\n\r\n<h2 style="border-bottom: 1px solid #aaaaaa; margin: 0px 0px 0.6em; color: black; background-image: none; font-weight: normal; padding-top: 0.5em; padding-bottom: 0.17em; font-size: 19px;"><span style="float: right; margin-left: 5px; font-size: 13px;">[<a style="text-decoration: none; color: #002bb8; background-image: none;" title="Edit section: See also" href="/w/index.php?title=Electric_charge&amp;action=edit&amp;section=8">edit</a>]</span><span>See also</span></h2>\r\n<ul style="margin: 0.3em 0px 0.5em 1.5em; padding: 0px; line-height: 1.5em; list-style-type: square; list-style-image: url(http://en.wikipedia.org/skins-1.5/monobook/bullet.gif);">\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Charge (physics)" href="/wiki/Charge_%28physics%29">Charge (physics)</a></li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Current density" href="/wiki/Current_density">Current density</a></li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electric field" href="/wiki/Electric_field">Electric field</a></li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electricity" href="/wiki/Electricity">Electricity</a></li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Electrostatic discharge" href="/wiki/Electrostatic_discharge">Electrostatic discharge</a></li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Elementary charge" href="/wiki/Elementary_charge">Elementary charge</a></li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Quantity of electricity" href="/wiki/Quantity_of_electricity">Quantity of electricity</a></li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="SI electromagnetism units" href="/wiki/SI_electromagnetism_units">SI electromagnetism units</a></li>\r\n	<li style="margin-bottom: 0.1em;"><a style="text-decoration: none; color: #002bb8; background-image: none;" title="Static electricity" href="/wiki/Static_electricity">Static electricity</a></li>\r\n</ul>\r\n</span>', 'page 3', '', 'publish', 'open', 'open', '', 'page-3', '', '', '2009-08-07 12:15:12', '2009-08-07 10:15:12', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?page_id=30', 0, 'page', '', 0);
INSERT INTO `wp1_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(3, 1, '2009-08-07 12:05:20', '2009-08-07 10:05:20', '<span style="color: #2c2c29; font-family: Tahoma; font-size: 11px; text-align: left;">\r\n<p style="border-width: 0px; margin: 1px 0px 10px; padding: 2px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-size: 1.2em;"><span style="border-width: 0px; margin: 0px; padding: 0px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-family: Arial;">Voir avec Pascal et Pol pour l’idée associée à ses actions pour un site d’une résolution 970 x 600</span></p>\r\n<p style="border-width: 0px; margin: 1px 0px 10px; padding: 2px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-size: 1.2em;"><span style="border-width: 0px; margin: 0px; padding: 0px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-family: Arial;">onRelease layout._parent._parent._parent._x=970</span></p>\r\n<p style="border-width: 0px; margin: 1px 0px 10px; padding: 2px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-size: 1.2em;">onRelease layout._parent._parent._parent._x=-970</p>\r\n<p style="border-width: 0px; margin: 1px 0px 10px; padding: 2px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-size: 1.2em;">onRelease layout._parent._parent._parent._y=600</p>\r\n<p style="border-width: 0px; margin: 1px 0px 10px; padding: 2px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-size: 1.2em;">onRelease layout._parent._parent._parent._y=-600</p>\r\n<p style="border-width: 0px; margin: 1px 0px 10px; padding: 2px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-size: 1.2em;">Pour une nav “particulière. Le probleme étant les animations liées au déplacement(s)</p>\r\n\r\n<p style="border-width: 0px; margin: 1px 0px 10px; padding: 2px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-size: 1.2em;"><span style="border-width: 0px; margin: 0px; padding: 0px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-family: Arial;">ZOOM NEGATIF\r\nonRelease layout._parent._parent._parent._xscale=100\r\nonRelease layout._parent._parent._parent._yscale=100\r\nonRelease layout._parent._parent._parent._x=0\r\nonRelease layout._parent._parent._parent._y=0</span><span style="border-width: 0px; margin: 0px; padding: 0px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-family: Arial;">ZOOM POSITIF</span>\r\n<span style="border-width: 0px; margin: 0px; padding: 0px; outline-width: 0px; vertical-align: baseline; background-color: transparent; font-family: Arial;">onRelease layout._parent._parent._parent._xscale=150\r\nonRelease layout._parent._parent._parent._yscale=150\r\nonRelease layout._parent._parent._parent._x=0\r\nonRelease layout._parent._parent._parent._y=0\r\nonRelease layout._parent._parent._parent._x=-250\r\nonRelease layout._parent._parent._parent._y=-200</span>\r\n\r\n</span>', 'article 1', '', 'publish', 'open', 'open', '', 'article-1', '', '', '2009-08-07 12:21:45', '2009-08-07 10:21:45', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?p=3', 0, 'post', '', 0),
(4, 1, '2009-08-07 12:01:41', '2009-08-07 10:01:41', '<span style="color: #cccccc; font-family: verdana; font-size: 14px;">\r\n<p style="margin: 0px; padding: 5px;">Et si je codais du design, hum ?  Ce blog est tellement stimulant que j’ai décidé d’arrêter Silex pour passer à Wordpress. Voilà de quoi combler mes futures soirées solitaires.</p>\r\n<p style="margin: 0px; padding: 5px;"><strong style="margin: 0px; padding: 0px;"><em style="margin: 0px; padding: 0px;">Mes  plugins Wordpress :</em></strong></p>\r\n<p style="margin: 0px; padding: 5px;"><strong style="margin: 0px; padding: 0px;"><a style="margin: 0px; padding: 0px; text-decoration: none; color: #eaab02;" href="http://blog.zen-dreams.com/fr/wordpress/zdmultilang/">ZdMultilang</a><span> </span>:<span> </span></strong>un plugin multilingue, simple à utiliser. Il propose une interface de traduction dans la zone des éditeurs. Sur le blog la traduction est automatiquement ajouté au post.</p>\r\n<p style="margin: 0px; padding: 5px;"><strong style="margin: 0px; padding: 0px;"><a style="margin: 0px; padding: 0px; text-decoration: none; color: #eaab02;" href="http://wordpress.org/extend/plugins/wp-piclens/">WP PicLens :</a></strong><span> </span>ce plugin permet de customiser l’ajout d’images dans un post. Une fois les images intégrées, un lien est ajouté au post. Ce lien ouvre un diaporama cooliris (requiert php5).</p>\r\n<p style="margin: 0px; padding: 5px;"><strong style="margin: 0px; padding: 0px;"><a style="margin: 0px; padding: 0px; text-decoration: none; color: #eaab02;" title="TDO Mini Forms" rel="wp27" href="http://wordpress.org/extend/plugins/tdo-mini-forms/">TDO Mini Forms</a><span> </span>:<span> </span></strong>pour soumettre un nouvel article sans passer par l’interface d’édition. Ce formulaire peut se trouver sur une simple page de votre blog. L’article soumit peut être ajouté directement au blog ou être archivé comme brouillon .</p>\r\n<p style="margin: 0px; padding: 5px;"><strong style="margin: 0px; padding: 0px;">Vive  PHP et  CSS, dans 6 mois je serais plus geek que geek !</strong></p>\r\n\r\n</span>', 'article 2', '', 'publish', 'open', 'open', '', 'article-2', '', '', '2009-08-07 12:18:15', '2009-08-07 10:18:15', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?p=4', 0, 'post', '', 1),
(5, 1, '2009-08-07 12:01:52', '2009-08-07 10:01:52', '<span style="color: #cccccc; font-family: verdana; font-size: 14px;">\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Bonjour,</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Voici un billet d’un vétéran du libre.  Il parle entre autres de la licence GPL et de son rapport aux entreprises et aux développeurs qui utilisent son travail.</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><a style="margin: 0px; padding: 0px; text-decoration: none; color: #eaab02;" href="http://zedshaw.com/blog/2009-07-13.html">http://zedshaw.com/blog/2009-07-13.html</a></p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Une citation :</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">“But the days of quick-flip corporations and ingrate programmers making money on my software are over. My new motto is:</p>\r\n\r\n<blockquote style="border: 1px dotted #3ba6e4; margin: 5px 15px; padding: 3px; font-size: 0.9em; text-align: justify;">\r\n<p style="margin: 0px; padding: 5px;"><em style="margin: 0px; padding: 0px;">Open source to open source, corporation to corporation.</em></p>\r\n</blockquote>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">If you do open source, you’re my hero and I support you. If you’re a corporation, let’s talk business.”</p>\r\n\r\n</span>', 'article 3', '', 'publish', 'open', 'open', '', 'article-3', '', '', '2009-08-07 12:18:31', '2009-08-07 10:18:31', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?p=5', 0, 'post', '', 0),
(6, 1, '2009-08-07 12:01:58', '2009-08-07 10:01:58', '', 'article 4', '', 'publish', 'open', 'open', '', 'article-4', '', '', '2009-08-07 12:01:58', '2009-08-07 10:01:58', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?p=6', 0, 'post', '', 0),
(7, 1, '2009-08-07 12:02:08', '2009-08-07 10:02:08', '<span style="color: #cccccc; font-family: verdana; font-size: 14px;">\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Bonjour,</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Voici un billet d’un vétéran du libre.  Il parle entre autres de la licence GPL et de son rapport aux entreprises et aux développeurs qui utilisent son travail.</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><a style="margin: 0px; padding: 0px; text-decoration: none; color: #eaab02;" href="http://zedshaw.com/blog/2009-07-13.html">http://zedshaw.com/blog/2009-07-13.html</a></p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Une citation :</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">“But the days of quick-flip corporations and ingrate programmers making money on my software are over. My new motto is:</p>\r\n\r\n<blockquote style="border: 1px dotted #3ba6e4; margin: 5px 15px; padding: 3px; font-size: 0.9em; text-align: justify;">\r\n<p style="margin: 0px; padding: 5px;"><em style="margin: 0px; padding: 0px;">Open source to open source, corporation to corporation.</em></p>\r\n</blockquote>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">If you do open source, you’re my hero and I support you. If you’re a corporation, let’s talk business.”</p>\r\n\r\n</span>', 'article 5', '', 'publish', 'open', 'open', '', 'article-5', '', '', '2009-08-07 12:19:24', '2009-08-07 10:19:24', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?p=7', 0, 'post', '', 0),
(8, 1, '2009-08-07 12:02:12', '2009-08-07 10:02:12', '<span style="color: #cccccc; font-family: verdana; font-size: 14px;"> </span>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Vous avez besoin de proposer une interface à vos utilisateurs mais vous manquez d’idées ? Pas de problème, on a regroupé des solutions utilisées dans les “user interface design patterns”.</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Un “design pattern” est une solution proposée pour un problème récurrent.  Le mieux c’est d’aller voir.</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Attention ce lien demande Silverlight.</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><a style="margin: 0px; padding: 0px; text-decoration: none; color: #eaab02;" href="http://quince.infragistics.com/">http://quince.infragistics.com/</a></p>', 'article 6', '', 'publish', 'open', 'open', '', 'article-6', '', '', '2009-08-07 12:19:46', '2009-08-07 10:19:46', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?p=8', 0, 'post', '', 0),
(9, 1, '2009-08-07 12:02:18', '2009-08-07 10:02:18', '<span style="color: #cccccc; font-family: verdana; font-size: 14px;">\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Merci à ceux qui sont venus d’être venu.</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Merci à Pol d’avoir tapé le compte rendu de cette réunion. Elle a été animée, effervescente, pleine d’idées. L’énergie de cette équipe est vraiment impressionnante, et l’envie de nous structurer pour la canaliser prouve que nous sommes tous sur la même longueur d’onde : il y a un truc de ouf à faire et c’est nous qui allons le faire!</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Voici donc ci-dessous un aperçu de la concurrence, la définition de notre cible et une liste d’objectifs stratégiques que nous devons ordonner et affiner. Ces objectifs vont influencer toutes nos actions dans les 6 prochains mois. Il nous faudra nous en imprégner pour tendre vers ces objectifs une fois dans le feu de l’action.</p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Le but est de commencer la mesure des indicateurs fin septembre et d’atteindre ces objectifs en janvier 2010 :</p>\r\n\r\n<ul style="margin: 5px 15px; padding: 0px; text-align: justify;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">juillet - aout - septembre = finalisation des objectifs en cours et mise en place des outils de mesure</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">octobre - novembre - décembre  = communication, présence médiatique.</li>\r\n</ul>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Lors de la prochaine réunion nous mettrons tout cela à plat et nous parlerons des objectifs opérationnels que nous envisageons. Il s’agira principalement de définir les  actions à entreprendre et de proposer des moyens de mesurer leur efficacité.</p>\r\n\r\n<div style="margin: 0px; padding: 0px; text-align: justify;"><dl id="attachment_1050" style="margin: 0px; padding: 0px; width: 393px;"><dt style="margin: 0px; padding: 0px;"><img style="margin: 0px; padding: 0px;" title="la réunion Silex &quot;Straté J&quot; a eu lieu dans le CNIT à la Défense" src="http://www.silexlabs.com/wp-content/uploads/2009/07/parldhi_hilton_paris_la_defense_gallery_welcome.jpg" alt="là où a eu lieu la réunion Silex &quot;Straté J&quot;" width="383" height="293" /></dt><dd style="margin: 0px; padding: 0px;"><em style="margin: 0px; padding: 0px;">La réunion Silex “Straté J” a eu lieu dans le CNIT à la Défense</em></dd></dl></div>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;"><span id="more-1045" style="margin: 0px; padding: 0px;"> </span></p>\r\n\r\n<h3 style="margin: 0px; padding: 0px; text-align: justify;">A. Concurrents</h3>\r\n<ul style="margin: 5px 15px; padding: 0px; text-align: justify;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Flash</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Flex</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Dreamweaver</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">CMS PHP</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Site builder</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Plateforme e-commerce</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Plateforme e-learning</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">web tv</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Facebook, myspace, …</li>\r\n</ul>\r\n<h3 style="margin: 0px; padding: 0px; text-align: justify;">B. Cibles</h3>\r\n<ul style="margin: 5px 15px; padding: 0px 0px 0px 30px; text-align: justify;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Webmaster</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Designer / Graphistes</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Chef de projet (en tant que prescripteur)</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Hébergeurs</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Agences de communication\r\n<ul style="margin: 5px 15px; padding: 0px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">com print</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">com web</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">événementiel</li>\r\n</ul>\r\n</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Grand public</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Leaders d’opinion\r\n<ul style="margin: 5px 15px; padding: 0px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">prescripteurs / lanceurs de buzz</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">presse / journaliste / bloggeurs</li>\r\n</ul>\r\n</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Editeurs logiciels</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Collectivités</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Ecoles\r\n<ul style="margin: 5px 15px; padding: 0px 0px 0px 30px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Graphismes</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">ingénieur</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<h3 style="margin: 0px; padding: 0px; text-align: justify;">C. Objectifs</h3>\r\n<ul style="margin: 5px 15px; padding: 0px; text-align: justify;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Augmenter de façon significative le nombre de téléchargement / utilisateurs</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Croissance du nombre réel d’utilisateurs : augmenter le taux de réalisation entre download et réelle utilisation.</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Etre plus présent dans les boites de com.</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Mesure du nombre d’utilisateurs manipulant Silex pendant les heures de bureaux (on suppose ici que ce sont des professionnels).</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Augmenter le nombre de sites “valorisants” pour Silex.</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Croissance internationale (US notamment - ne pas négliger les pays émergents (Inde - Brésil) - ne pas oublier la Chine)</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Dénombrer le nombre de produits (site / cd…) finalisé en 2008 - 2009 avec Silex afin d’augmenter leur nombre en 2010.</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Définir des packages - niveau de proposition.</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Garder notre rang sur Sourceforge.</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Rester leader dans l’open source Flash</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Créer un buzz - bruit médiatique (octobre / novembre / décembre 2009).</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Commencer la mise en place d’une plateforme d’échange</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Augmenter notre veille technologique (concurrence / nouveautés…).</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Rendre Silex plus accessible / plus simple pour les débutants.</li>\r\n</ul>\r\n</span>', 'article 7', '', 'publish', 'open', 'open', '', 'article-7', '', '', '2009-08-07 12:20:18', '2009-08-07 10:20:18', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?p=9', 0, 'post', '', 0),
(10, 1, '2009-08-07 12:02:26', '2009-08-07 10:02:26', '<span style="color: #cccccc; font-family: verdana; font-size: 14px;">\r\n<p style="margin: 0px; padding: 5px;">Une question qu’on entend de temps à autre, maintenant que Silex a du succès, est comment se faire connaître. Je suis tombé sur cet article (en anglais, désolé) qui traite de la question des rapports avec la presse, les sites d’informations, etc.</p>\r\n<p style="margin: 0px; padding: 5px;"><a style="margin: 0px; padding: 0px; text-decoration: none; color: #eaab02;" href="http://www.linux.com/archive/feature/35549">http://www.linux.com/archive/feature/35549</a></p>\r\n\r\n</span>', 'article 8', '', 'publish', 'open', 'open', '', 'article-8', '', '', '2009-08-07 12:20:31', '2009-08-07 10:20:31', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?p=10', 0, 'post', '', 0),
(11, 1, '2009-08-07 12:02:30', '2009-08-07 10:02:30', '<span style="color: #cccccc; font-family: verdana; font-size: 14px;">\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Un post consacré au benjamin de la Team Silex,<span> </span><a style="margin: 0px; padding: 0px; text-decoration: none; color: #eaab02;" href="http://silexprod.com/arthur/#default/start/accueil" target="_blank">Arthur</a>, dont le stage sous la direction de<a style="margin: 0px; padding: 0px; text-decoration: none; color: #eaab02;" href="http://silexprod.com/super/#pol-goasdoue/design.lab/portail" target="_blank">Pol Goasdoué</a><span> </span>s’est terminé ces jours-ci…</p>\r\n<p style="margin: 0px; padding: 5px; text-align: center;"><img style="margin: 0px; padding: 0px;" title="Arthur Silex Rose" src="http://www.silexlabs.com/wp-content/uploads/2009/07/02072009680-1024x768.jpg" alt="Arthur Silex Rose" width="573" height="430" /></p>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Arthur vient de valider son rapport de stage consacré à sa participation au sein de l’équipe Silex : des<span> </span><span style="margin: 0px; padding: 0px; color: #ff00ff;"><span style="margin: 0px; padding: 0px; color: #ff9900;">templates</span><span> </span></span>pour<span> </span><span style="margin: 0px; padding: 0px; color: #3366ff;">Chaivoo</span>, les<span style="margin: 0px; padding: 0px; color: #ff0000;"><span style="margin: 0px; padding: 0px; color: #800080;"><span> </span>icônes</span><span> </span></span>du Manager, le silex<span> </span><span style="margin: 0px; padding: 0px; color: #99cc00;">widget</span>, les<span style="margin: 0px; padding: 0px; color: #ff00ff;">playlists</span>. A seulement<span> </span><span style="margin: 0px; padding: 0px; color: #ff0000;">18 ans</span>, Arthur a su jongler avec Silex et trouver sa place au sein de l’équipe pendant ce mois de mai 2009 à la Défense, la tête pleine de projets.</p>\r\n\r\n<div style="margin: 0px; padding: 0px; text-align: justify;"><dl id="attachment_1011" style="margin: 0px; padding: 0px; width: 501px;"><dt style="margin: 0px; padding: 0px 0px 0px 90px;"><img style="margin: 0px; padding: 0px;" title="Arthur Silex Rose Rapport de Stage" src="http://www.silexlabs.com/wp-content/uploads/2009/07/un-garcon-plein-davenir-rapport-stage-blog-silex-1024x487.png" alt="un-garcon-plein-davenir-rapport-stage-blog-silex" width="491" height="234" /></dt><dd style="margin: 0px; padding: 0px 0px 0px 180px; text-align: center;"><em style="margin: 0px; padding: 0px;">Extrait du rapport de stage d’Arthur<br style="margin: 0px; padding: 0px;" /></em></dd></dl></div>\r\n<p style="margin: 0px; padding: 5px; text-align: justify;">Nous te souhaitons une bonne continuation à Silex et partout ailleurs, de Cuba à New York, de Paris à Tombouctou ;D</p>\r\n<p style="margin: 0px; padding: 5px; text-align: center;"><strong style="margin: 0px; padding: 0px;"><span style="margin: 0px; padding: 0px; color: #800080;"><span style="margin: 0px; padding: 0px; color: #ff0000;">KudoS<span> </span></span><span style="margin: 0px; padding: 0px; color: #ff00ff;"><span style="margin: 0px; padding: 0px; color: #ff00ff;">ARTHUR</span><span> </span></span><span style="margin: 0px; padding: 0px; color: #3366ff;">and</span><span style="margin: 0px; padding: 0px; color: #ff9900;"><span> </span>KeeP<span> </span></span>in<span> </span><span style="margin: 0px; padding: 0px; color: #99cc00;">ToucH !</span></span></strong></p>\r\n\r\n</span>', 'article 9', '', 'publish', 'open', 'open', '', 'article-9', '', '', '2009-08-07 12:21:00', '2009-08-07 10:21:00', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?p=11', 0, 'post', '', 0),
(12, 1, '2009-08-07 12:02:37', '2009-08-07 10:02:37', '<span style="color: #cccccc; font-family: verdana; font-size: 14px;">\r\n<div style="margin: 0px; padding: 0px;">Les objectifs de cet été pour la team :</div>\r\n<p style="margin: 0px; padding: 5px;"></p>\r\n\r\n<div style="margin: 0px; padding: 0px;">\r\n<ul style="margin: 5px 15px; padding: 0px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Ath\r\n<ul style="margin: 5px 15px; padding: 0px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">statuts de l’asso : remanier et déposer</li>\r\n</ul>\r\n</li>\r\n<p style="margin: 0px; padding: 5px;"></p>\r\n\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Flo\r\n<ul style="margin: 5px 15px; padding: 0px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">partenariats</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">continuer com (journalistes)</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">continuer évènements / concours</li>\r\n</ul>\r\n</li>\r\n<p style="margin: 0px; padding: 5px;"></p>\r\n\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Pol et Pascal\r\n<ul style="margin: 5px 15px; padding: 0px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">thème portfolio</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">thème wordpress pour dev plugin wordpress</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">design de la nouvelle documentation</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">continuer sur le forum (pol)</li>\r\n</ul>\r\n</li>\r\n<p style="margin: 0px; padding: 5px;"></p>\r\n\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Arth\r\n<ul style="margin: 5px 15px; padding: 0px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">thèmes</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Chaivoo</li>\r\n</ul>\r\n</li>\r\n<p style="margin: 0px; padding: 5px;"></p>\r\n\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Ariel\r\n<ul style="margin: 5px 15px; padding: 0px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">gérer les contributeurs dev et le forum</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">mettre en place des outils de stat pour manager, install…</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">commencer le système de plugin de silex</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">chaivoo?</li>\r\n</ul>\r\n</li>\r\n<p style="margin: 0px; padding: 5px;"></p>\r\n\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Lex\r\n<ul style="margin: 5px 15px; padding: 0px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">plugin silex pour wordpress</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">organisation / gestion de l’équipe</li>\r\n</ul>\r\n</li>\r\n<p style="margin: 0px; padding: 5px;"></p>\r\n\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Sha\r\n<ul style="margin: 5px 15px; padding: 0px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">finir le manager</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">réfléchir à la nouvelle doc</li>\r\n</ul>\r\n</li>\r\n<p style="margin: 0px; padding: 5px;"></p>\r\n\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">Oph\r\n<ul style="margin: 5px 15px; padding: 0px;">\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">gérer les contributeurs trad</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">trad de l’aide du manager en anglais</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">news silex</li>\r\n	<li style="margin: 0px; padding: 0px 0px 0px 13px; list-style-type: none; background-image: url(http://www.silexlabs.com/wp-content/themes/vistered-little/images/star.png); background-repeat: no-repeat; background-position: 0px 2px;">commencer le chantier “aide” (rewrite? trad?)</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</div>\r\n</span>', 'article 10', '', 'publish', 'open', 'open', '', 'article-10', '', '', '2009-08-07 12:21:21', '2009-08-07 10:21:21', '', 0, 'http://localhost/dev/1silex4wordpress/wordpress/?p=12', 0, 'post', '', 0),
